import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Button, Form, InputGroup } from "react-bootstrap";
import "./styles.css";

function LoginPage() {
  const [validated, setValidated] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);

    // Handle form submission here (e.g., send data to an API or perform authentication)
  };

  const containerStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
  };

  const boxStyle = {
    backgroundColor: "#f0f0f0",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0, 0, 0, 0.2)",
    maxWidth: "450px",
    width: "100%",
  };

  const buttonStyle = {
    marginTop: "50px",
    margin: "0 auto",
  };

  return (
    <div>
    <div>
      <h2 className="subheader">LoginPage</h2>
      <div className="button-container">
        <button className="main-button">
          <Link className="navlink" to="/">
            Back to Home
          </Link>
        </button>
      </div>
      <div style={{containerStyle}}>
        <div style={{boxStyle}}>
          <h3>Login</h3>
          <Form noValidate validated={validated} onSubmit={handleSubmit}>
            <Form.Group controlId="username">
              <Form.Label>Username</Form.Label>
              <InputGroup hasValidation>
                <Form.Control
                  required
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  placeholder="Enter your username"
                  isInvalid={validated && !formData.username}
                />
                <Form.Control.Feedback type="invalid">
                  Please enter a valid username.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>

            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <InputGroup hasValidation>
                <Form.Control
                  required
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                  isInvalid={validated && !formData.password}
                />
                <Form.Control.Feedback type="invalid">
                  Please enter a valid password.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>
            <br></br>
            <div className="text-center">
              <Button variant="success" type="submit" style={buttonStyle}>
                Login
              </Button>
            </div>
          </Form>
        </div>
      </div>
    </div>
    </div>
  );
}

export default LoginPage;
