import React, { useState, useEffect } from "react";
import "./styles.css";
import Form from "react-bootstrap/Form";

function Bidding() {
  const [itemName, setItemName] = useState("Painting");
  const [itemDesc, setItemDesc] = useState(
    "Antique painting by famous artist XYZ. Painted in 1950."
  );
  const [sName, setSName] = useState("Mr.Seller");
  const [itemTags, setItemTags] = useState(["painting", "antique", "physical"]);
  const [sRating, setSRating] = useState(4.2);
  const [status, setStatus] = useState("ONGOING");
  const [currentBid, setCurrentBid] = useState(2000);
  const [inputValue, setInputValue] = useState("");
  const [bidAmount, setBidAmount] = useState(0);
  const [bidTimer, setBidTimer] = useState(60);
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });
  const [balance, setBalance] = useState(5000);
  const [successModal, setSuccessModal] = useState(false);
  const [timeOutModal, setTimeOutModal] = useState(false);
  const [noNullModal, setNoNullModal] = useState(false);
  const [higherBidModal, setHigherBidModal] = useState(false);
  const [noBalanceModal, setNoBalanceModal] = useState(false);

  useEffect(() => {
    const targetDate = new Date("2023-10-22T23:59:59");
    const updateRemainingTime = () => {
      const currentDate = new Date();
      const timeDiff = targetDate - currentDate;
      const days = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
      const hours = Math.floor(
        (timeDiff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
      );
      const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((timeDiff % (1000 * 60)) / 1000);
      setTimeLeft({ days, hours, minutes, seconds });
    };
    const intervalId = setInterval(updateRemainingTime, 1000);
    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const timerInterval = setInterval(() => {
      if (bidTimer > 0) {
        setBidTimer((prevTime) => prevTime - 1);
      } else {
        setStatus("OVER");
        clearInterval(timerInterval);
        openModal("timeOut");
      }
    }, 1000);

    return () => {
      clearInterval(timerInterval);
    };
  }, [bidTimer]);

  const closeModal = () => {
    setSuccessModal(false);
    setNoNullModal(false);
    setNoBalanceModal(false);
    setTimeOutModal(false);
    setHigherBidModal(false);
  };

  const chkInput = () => {
    if (bidTimer === 0) {
      setTimeOutModal(!timeOutModal);
      return false;
    } else if (inputValue > balance) {
      setNoBalanceModal(!noBalanceModal);
      return false;
    } else if (inputValue === null || inputValue === 0) {
      setNoNullModal(!noNullModal);
      return false;
    } else if (inputValue <= currentBid) {
      setHigherBidModal(!higherBidModal);
      return false;
    } else {
      return true;
    }
  };

  const updateBids = () => {
    setCurrentBid(inputValue);
    setBalance(balance - inputValue);
    setBidTimer(60);
  };

  const submit = () => {
    if (chkInput()) {
      updateBids();
      setSuccessModal(!successModal);
    }
  };

  return (
    <div className="bidContainer">
      <div className="biddingBox">
        <div className="imageInfo">
          <div>
            <img src="/images/user.jpg" className="image" alt="Item" />
          </div>
        </div>

        <div className="bidUserInfo">
          <div className="bidInfo">
            <div className="sellerDetails">
              <p id="SName">Seller: {sName}</p>
              <p id="SRating" style={{ fontSize: "20px" }}>
                Rating: {sRating}
              </p>
            </div>

            <div className="itemDetails">
              <div className="tags">
                {itemTags.map((tag, index) => (
                  <span key={index} className="tag">
                    {tag}
                  </span>
                ))}
              </div>
              <p id="itemName">Product: {itemName}</p>
              <p id="itemDesc" style={{ fontSize: "20px" }}>
                {itemDesc}
              </p>
              <p id="bidStatus">Status: {status}</p>
            </div>

            <div className="timeInfo">
              <p id="timeLeft">
                Time Remaining: {timeLeft.days}:{timeLeft.hours}:
                {timeLeft.minutes}:{timeLeft.seconds}
              </p>
            </div>
          </div>

          <div className="userInfo">
            <p id="balance">Your Balance: {balance} currency</p>
            <div className="bidAmount">
              <p id="currentBid" style={{ width: "50%" }}>
                Current Bid: {currentBid}
              </p>
              <div className="bidInput">
                <form style={{ display: "flex", flexDirection: "row" }}>
                  <p>Your Bid:&nbsp;</p>
                  <Form.Group style={{ width: "55%" }} controlId="input">
                    <Form.Control
                      required
                      type="number"
                      placeholder={0}
                      value={inputValue}
                      onChange={(e) => setInputValue(e.target.value)}
                    />
                  </Form.Group>
                </form>
              </div>
            </div>

            <div className="placeBidTimer">
              <button
                className="placeBid"
                id="openModalButton"
                onClick={submit}
              >
                Place Bid
              </button>
              <p>{bidTimer}</p>
            </div>

            {successModal && (
              <div class="overlay" id="overlay">
                <div class="modal" id="modal">
                  <div className={modalType}>
                    <span class="close" id="closeButton" onClick={closeModal}>
                      &times;
                    </span>
                    <h2>SUCCESS</h2>
                    <p>Bid of {inputValue} successfully placed!!</p>
                  </div>
                </div>
              </div>
            )}

            {noNullModal && (
              <div class="overlay" id="overlay">
                <div class="modal" id="modal">
                  <div className={modalType}>
                    <span class="close" id="closeButton" onClick={closeModal}>
                      &times;
                    </span>
                    <h2>INVALID INPUT</h2>
                    <p>Please enter a valid bid value!!</p>
                  </div>
                </div>
              </div>
            )}

            {}

            {}

            {}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Bidding;
