import React, { useState, useEffect } from "react";
import "./styles.css";
import Form from "react-bootstrap/Form";
import { BrowserRouter, Link, Routes } from "react-router-dom";
import Row from "react-bootstrap/esm/Row";

function Bidding() {
  const itemName = "Painting";
  const itemDesc = "Antique painting by famous artist XYZ. Painted in 1950.";
  const sName = "Mr.Seller";
  const itemTags = ["painting", "antique", "physical"];
  const sRating = 4.2;
  const [status, setStatus] = useState("ONGOING");
  const [currentBid, setCurrentBid] = useState(2000);
  const [inputValue, setInputValue] = useState("");
  const [bidAmount, setBidAmount] = useState(0);
  const [bidTimer, setBidTimer] = useState(60);
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });
  const [balance, setBalance] = useState(5000);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [popupState, setPopupState] = useState(false);

  useEffect(() => {
    const targetDate = new Date("2023-10-22T23:59:59"); //YYYY-MM-DDTHH:MM:SS
    const updateRemainingTime = () => {
      const currentDate = new Date();
      const timeDiff = targetDate - currentDate;
      const days = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
      const hours = Math.floor(
        (timeDiff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
      );
      const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((timeDiff % (1000 * 60)) / 1000);
      setTimeLeft({ days, hours, minutes, seconds });
    };
    const intervalId = setInterval(updateRemainingTime, 1000);
    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const timerInterval = setInterval(() => {
      if (bidTimer > 0) {
        setBidTimer((prevTime) => prevTime - 1);
      } else {
        setStatus("OVER");
        clearInterval(timerInterval); // Stop the timer when it reaches 0
      }
    }, 1000); // Update every 1 second

    return () => {
      clearInterval(timerInterval); // Clean up the interval on unmount
    };
  }, [bidTimer]);

  const overlay = document.getElementById("overlay");
  const modal = document.getElementById("modal");
  const closeButton = document.getElementById("closeButton");
  const success = document.getElementById("success");
  const noNull = document.getElementById("noNull");
  const noBalance = document.getElementById("noBalance");
  const timeOut = document.getElementById("timeOut");
  const higherBid = document.getElementById("higherBid");

  const openModal = () => {
    overlay.style.display = "flex";
    modal.style.display = "flex";
    closeButton.style.display = "flex";
  };

  const closeModal = () => {
    overlay.style.display = "none";
    modal.style.display = "none";
    closeButton.style.display = "none";
  };

  const timeOutModal = () => {
    overlay.style.display = "flex";
    modal.style.display = "flex";
    closeButton.style.display = "flex";
    timeOut.style.display = "flex";
  };

  const noBalanceModal = () => {
    overlay.style.display = "flex";
    modal.style.display = "flex";
    closeButton.style.display = "flex";
    noBalance.style.display = "flex";
  };

  const noNullModal = () => {
    overlay.style.display = "flex";
    modal.style.display = "flex";
    closeButton.style.display = "flex";
    noNull.style.display = "flex";
  };

  const successModal = () => {
    overlay.style.display = "flex";
    modal.style.display = "flex";
    closeButton.style.display = "flex";
    noBalance.style.display = "flex";
  };

  const higherBidModal = () => {
    overlay.style.display = "flex";
    modal.style.display = "flex";
    closeButton.style.display = "flex";
    higherBid.style.display = "flex";
  };

  const chkInput = () => {
    if (bidTimer == 0) {
      timeOutModal();
      return false;
    } else if (inputValue > balance) {
      noBalanceModal();
      return false;
    } else if (inputValue == null || inputValue == 0) {
      noNullModal();
      return false;
    } else if (inputValue <= currentBid) {
      higherBidModal();
      return false;
    } else {
      sc
      return true;
    }
  };

  const updateBids = () => {
    setCurrentBid(inputValue);
    setBalance(balance - inputValue);
    setBidTimer(60);
  };

  const submit = () => {
    if (chkInput()) {
      openModal();
      successModal();
      updateBids();
    }
  };

  return (
    <div className="bidContainer">
      <div className="biddingBox">
        <div className="imageInfo">
          <div>
            <img src="/images/user.jpg" className="image" alt="Item" />
          </div>
        </div>

        <div className="bidUserInfo">
          <div className="bidInfo">
            <div className="sellerDetails">
              <p id="SName">Seller: {sName}</p>
              <p id="SRating" style={{ fontSize: "20px" }}>
                Rating: {sRating}
              </p>
            </div>

            <div className="itemDetails">
              <div className="tags">
                {itemTags.map((tag, index) => (
                  <span key={index} className="tag">
                    {tag}
                  </span>
                ))}
              </div>
              <p id="itemName">Product: {itemName}</p>
              <p id="itemDesc" style={{ fontSize: "20px" }}>
                {itemDesc}
              </p>
              <p id="bidStatus">Status: {status}</p>
            </div>

            <div className="timeInfo">
              <p id="timeLeft">
                Time Remaining: {timeLeft.days}:{timeLeft.hours}:
                {timeLeft.minutes}:{timeLeft.seconds}
              </p>
            </div>
          </div>

          <div className="userInfo">
            <p id="balance">Your Balance: {balance} currency</p>
            <div className="bidAmount">
              <p id="currentBid" style={{ width: "50%" }}>
                Current Bid: {currentBid}
              </p>
              <div className="bidInput">
                <form style={{ display: "flex", flexDirection: "row" }}>
                  <p>Your Bid:&nbsp;</p>
                  <Form.Group style={{ width: "55%" }} controlId="input">
                    <Form.Control
                      required
                      type="text"
                      placeholder={0}
                      value={inputValue}
                      onChange={(e) => setInputValue(e.target.value)}
                    />
                  </Form.Group>
                </form>
              </div>
            </div>

            <div className="placeBidTimer">
              <button
                className="placeBid"
                id="openModalButton"
                onClick={submit}
              >
                Place Bid
              </button>
              <p>{bidTimer}</p>
            </div>

            <div class="overlay" id="overlay">
              <div class="modal" id="modal">
                <div className="success" id="success">
                  <span class="close" id="closeButton" onClick={closeModal}>
                    &times;
                  </span>
                  <h2>SUCCESS</h2>
                  <p>Bid of {inputValue} successfully placed!!</p>
                </div>

                <div className="noNull" id="success">
                  <span class="close" id="closeButton" onClick={closeModal}>
                    &times;
                  </span>
                  <h2>INVALID INPUT</h2>
                  <p>Please enter a valid bid value!!</p>
                </div>

                <div className="noBalance" id="noBalance">
                  <span class="close" id="closeButton" onClick={closeModal}>
                    &times;
                  </span>
                  <h2>NOT ENOUGH BALANCE</h2>
                  <p>You do not have sufficient balance!!</p>
                </div>

                <div className="timeOut" id="timeOut">
                  <span class="close" id="closeButton" onClick={closeModal}>
                    &times;
                  </span>
                  <h2>TIME OUT</h2>
                  <p>The bid is over!!</p>
                </div>

                <div className="higherBid" id="higherBid">
                  <span class="close" id="closeButton" onClick={closeModal}>
                    &times;
                  </span>
                  <h2>LOWER BID PLACED</h2>
                  <p>Please place a bid higher than {currentBid}!!</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

