import React, { useState, useEffect } from "react";
import "./styles.css";
import Form from "react-bootstrap/Form";
import { BrowserRouter, Link, Routes } from "react-router-dom";
import Row from "react-bootstrap/esm/Row";

function Bidding() {
  const balance = 5000;
  const itemName = "Painting";
  const itemDesc = "Antique painting by famous artist XYZ. Painted in 1950.";
  const sName = "Mr.Seller";
  const itemTags = ["painting", "antique", "physical"];
  const sRating = 4.2;
  const status = "ONGOING";
  const currentBid = 2000;
  const placeholder = currentBid + 1;

  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [popupState, setPopupState] = useState(false);

  useEffect(() => {
    const targetDate = new Date("2023-12-31T23:59:59");

    const updateRemainingTime = () => {
      const currentDate = new Date();
      const timeDiff = targetDate - currentDate;

      const days = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
      const hours = Math.floor(
        (timeDiff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
      );
      const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((timeDiff % (1000 * 60)) / 1000);

      setTimeLeft({ days, hours, minutes, seconds });
    };

    const intervalId = setInterval(updateRemainingTime, 1000);

    // Ensure you clear the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);

  const overlay = document.getElementById("overlay");
  const modal = document.getElementById("modal");
  const closeButton = document.getElementById("closeButton");

  const openModal = () => {
    overlay.style.display = "block";
    modal.style.display = "block";
    closeButton.style.display = "block";
  };

  const closeModal = () => {
    overlay.style.display = "none";
    modal.style.display = "block";
    closeButton.style.display = "block";
  };

  return (
    <div className="bidContainer">
      <div className="biddingBox">
        <div className="imageInfo">
          <div>
            <img src="/images/user.jpg" className="image" alt="Item" />
          </div>
        </div>
        <div className="bidUserInfo">
          <div className="bidInfo">
            <div className="sellerDetails">
              <p id="SName">Seller: {sName}</p>
              <p id="SRating" style={{ fontSize: "20px" }}>
                Rating: {sRating}
              </p>
            </div>
            <div className="itemDetails">
              <div className="tags">
                {itemTags.map((tag, index) => (
                  <span key={index} className="tag">
                    {tag}
                  </span>
                ))}
              </div>
              <p id="itemName">Product: {itemName}</p>
              <p id="itemDesc" style={{ fontSize: "20px" }}>
                {itemDesc}
              </p>
              <p id="bidStatus">Status: {status}</p>
            </div>
            <div className="timeInfo">
              <p id="timeLeft">
                Time Remaining: {timeLeft.days}:{timeLeft.hours}:
                {timeLeft.minutes}:{timeLeft.seconds}
              </p>
            </div>
          </div>
          <div className="userInfo">
            <p id="balance">Your Balance: {balance} currency</p>
            <div className="bidAmount">
              <p id="currentBid" style={{width:"50%"}}>Current Bid: {currentBid}</p>
              <div className="bidInput">
                <form style={{display:"flex", flexDirection:"row"}}>
                  <p>Your Bid:</p>
                  <Form.Group style={{width:"50%"}}>
                    <Form.Control type="text" placeholder={placeholder} />
                  </Form.Group>
                </form>
              </div>
            </div>
            <button
              className="placeBid"
              id="openModalButton"
              onClick={openModal}
            >
              Place Bid
            </button>
            <div class="overlay" id="overlay">
              <div class="modal" id="modal">
                <span class="close" id="closeButton" onClick={closeModal}>
                  &times;
                </span>
                <h2>Modal Content</h2>
                <p>
                  This is the content of the modal. You can place any content
                  here.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Bidding;
