import React, { useState, useEffect } from "react";
import "./styles.css";
import Form from "react-bootstrap/Form";

function Bidding() {
  const itemName = "Painting";
  const itemDesc = "Antique painting by famous artist XYZ. Painted in 1950.";
  const sName = "Mr.Seller";
  const itemTags = ["painting", "antique", "physical"];
  const sRating = 4.2;
  const [status, setStatus] = useState("ONGOING");
  const [currentBid, setCurrentBid] = useState(2000);
  const [inputValue, setInputValue] = useState("");
  const [bidAmount, setBidAmount] = useState(0);
  const [bidTimer, setBidTimer] = useState(60);
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });
  const [balance, setBalance] = useState(5000);
  const [modalType, setModalType] = useState(null);

  useEffect(() => {
    const targetDate = new Date("2023-10-22T23:59:59");
    const updateRemainingTime = () => {
      const currentDate = new Date();
      const timeDiff = targetDate - currentDate;
      const days = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
      const hours = Math.floor((timeDiff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((timeDiff % (1000 * 60)) / 1000);
      setTimeLeft({ days, hours, minutes, seconds });
    };
    const intervalId = setInterval(updateRemainingTime, 1000);
    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const timerInterval = setInterval(() => {
      if (bidTimer > 0) {
        setBidTimer((prevTime) => prevTime - 1);
      } else {
        setStatus("OVER");
        clearInterval(timerInterval);
      }
    }, 1000);

    return () => {
      clearInterval(timerInterval);
    };
  }, [bidTimer]);

  const openModal = (type) => {
    setModalType(type);
  };

  const closeModal = () => {
    setModalType(null);
  };

  const chkInput = () => {
    if (bidTimer === 0) {
      openModal("timeOut");
      return false;
    } else if (inputValue > balance) {
      openModal("noBalance");
      return false;
    } else if (inputValue == null || inputValue == 0) {
      openModal("noNull");
      return false;
    } else if (inputValue <= currentBid) {
      openModal("higherBid");
      return false;
    } else {
      return true;
    }
  };

  const updateBids = () => {
    setCurrentBid(inputValue);
    setBalance(balance - inputValue);
    setBidTimer(60);
  };

  const submit = () => {
    if (chkInput()) {
      openModal("success");
      updateBids();
    }
  };

  return (
    <div className="bidContainer">
      <div className="biddingBox">
        <div className="imageInfo">
          <div>
            <img src="/images/user.jpg" className="image" alt="Item" />
          </div>
        </div>

        <div className="bidUserInfo">
          <div className="bidInfo">
            <div className="sellerDetails">
              <p>Seller: {sName}</p>
              <p style={{ fontSize: "20px" }}>Rating: {sRating}</p>
            </div>

            <div className="itemDetails">
              <div className="tags">
                {itemTags.map((tag, index) => (
                  <span key={index} className="tag">
                    {tag}
                  </span>
                ))}
              </div>
              <p>Product: {itemName}</p>
              <p style={{ fontSize: "20px" }}>{itemDesc}</p>
              <p>Status: {status}</p>
            </div>

            <div className="timeInfo">
              <p>
                Time Remaining: {timeLeft.days}:{timeLeft.hours}:
                {timeLeft.minutes}:{timeLeft.seconds}
              </p>
            </div>
          </div>

          <div className="userInfo">
            <p>Your Balance: {balance} currency</p>
            <div className="bidAmount">
              <p style={{ width: "50%" }}>Current Bid: {currentBid}</p>
              <div className="bidInput">
                <form style={{ display: "flex", flexDirection: "row" }}>
                  <p>Your Bid:&nbsp;</p>
                  <Form.Group style={{ width: "55%" }} controlId="input">
                    <Form.Control
                      required
                      type="text"
                      placeholder={0}
                      value={inputValue}
                      onChange={(e) => setInputValue(e.target.value)}
                    />
                  </Form.Group>
                </form>
              </div>
            </div>

            <div className="placeBidTimer">
              <button className="placeBid" onClick={submit}>
                Place Bid
              </button>
              <p>{bidTimer}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Bidding;

/*
    Apply success animation
    update balance and current bid <DONE>
    add 30sec timer beside place bid button
    tags ko thik kro <DONE>
    */
