import React, { useState, useEffect } from "react";
import "./styles.css";
import { BrowserRouter, Link, Routes } from "react-router-dom";

function Bidding() {
  const [balance, setBalance] = useState(5000);
  const [itemName, setItemName] = useState("Painting");
  const [itemDesc, setItemDesc] = useState(
    "Antique painting by famous artist XYZ. Painted in 1950."
  );
  const [sName, setSName] = useState("Mr.Seller");
  const [itemTags, setItemTags] = useState(["painting", "antique", "physical"]);
  const [sRating, setSRating] = useState(4.2);
  const [timeLeft, setTimeLeft] = useState({ days: 0, hours: 0, minutes: 0, seconds: 0 });

  useEffect(() => {
    const targetDate = new Date("2023-12-31T23:59:59");

    const updateRemainingTime = () => {
      const currentDate = new Date();
      const timeDiff = targetDate - currentDate;

      const days = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
      const hours = Math.floor((timeDiff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((timeDiff % (1000 * 60)) / 1000);

      setTimeLeft({ days, hours, minutes, seconds });
    };

    const intervalId = setInterval(updateRemainingTime, 1000);

    // Ensure you clear the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="bidContainer">
      <div className="biddingBox">
        <div className="imageInfo">
          <div>
            <img src="/images/user.jpg" className="image" alt="Item" />
          </div>
        </div>
        <div className="bidUserInfo">
          <div className="bidInfo">
            <div className="sellerDetails">
              <p id="SName">Seller: {sName}</p>
              <p id="SRating" style={{ fontSize: "20px" }}>
                Rating: {sRating}
              </p>
            </div>
            <div className="itemDetails">
              <div className="tags">
                {itemTags.map((tag, index) => (
                  <span key={index} className="tag">
                    {tag}
                  </span>
                ))}
              </div>
              <p id="itemName">Product: {itemName}</p>
              <p id="itemDesc" style={{ fontSize: "20px" }}>
                {itemDesc}
              </p>
            </div>
            <div className="timeInfo">
              <p id="timeLeft">
                Time Remaining: {timeLeft.days}:{timeLeft.hours}: {timeLeft.minutes}:{timeLeft.seconds} seconds
              </p>
            </div>
          </div>
          <hr />

          <div className="userInfo">
            <p id="balance">User Balance: {balance} currency</p>
          </div>
          <hr />
        </div>
      </div>
    </div>
  );
}

export default Bidding;
