import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Button, Form, InputGroup } from "react-bootstrap";
import "./styles.css"; // Import your external CSS file

function LoginPage() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }

    // Handle form submission here (e.g., send data to an API or perform authentication)
  };

  return (
    <div>
      <h2 className="subheader">LoginPage</h2>
      <div className="button-container">
        <button className="main-button">
          <Link className="header h5" to="/">Back to Home</Link>
        </button>
      </div>
      <div className="containerStyle">
        <div className="boxStyle">
          <h3>Login</h3>
          <Form noValidate onSubmit={handleSubmit}>
            <Form.Group controlId="username">
              <Form.Label>Username</Form.Label>
              <InputGroup hasValidation>
                <Form.Control
                  required
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleInputChange}
                  placeholder="Enter your username"
                />
                <Form.Control.Feedback type="invalid">
                  Please enter a valid username.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>

            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <InputGroup hasValidation>
                <Form.Control
                  required
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter your password"
                />
                <Form.Control.Feedback type="invalid">
                  Please enter a valid password.
                </Form.Control.Feedback>
              </InputGroup>
            </Form.Group>
            <div className="text-center">
              <Button variant="success" type="submit">
                Login
              </Button>
            </div>
          </Form>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
