import React, {useState } from "react";
import './styles.css';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
//import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
//import Row from 'react-bootstrap/Row';

const LoginPage = () => {
  const [validated, setValidated] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);

    // Handle form submission here (e.g., send data to an API or perform authentication)
  };

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
  };

  const boxStyle = {
    backgroundColor: '#f0f0f0',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)',
    maxWidth: '450px',
    width: '100%',
  };

  const buttonStyle = {
    marginTop: '50px', // Adjust the top margin for vertical centering
    margin: '0 auto', // Center the button horizontally*
  };

  return (
    <body class="body">
    <div>
     <h2 class="subheader">LoginPage</h2>
     {/* Add your sign-up form ,<Link to="/">Back to Home</Link> */}
     <div class="button-container">
     <button class="main-button">
     <Link class="header h5" to="/">Back to Home</Link>
     </button>
     </div>
    </div>
    <div style={containerStyle}>
      <div style={boxStyle}>
        <h3>Login</h3>
        <Form noValidate validated={validated} onSubmit={handleSubmit}>
          <Form.Group controlId="username">
            <Form.Label>Username</Form.Label>
            <InputGroup hasValidation>
              <Form.Control
                required
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                placeholder="Enter your username"
                isInvalid={validated && !formData.username}
              />
              <Form.Control.Feedback type="invalid">
                Please enter a valid username.
              </Form.Control.Feedback>
            </InputGroup>
          </Form.Group>

          <Form.Group controlId="password">
            <Form.Label>Password</Form.Label>
            <InputGroup hasValidation>
              <Form.Control
                required
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="Enter your password"
                isInvalid={validated && !formData.password}
              />
              <Form.Control.Feedback type="invalid">
                Please enter a valid password.
              </Form.Control.Feedback>
            </InputGroup>
          </Form.Group>
          <br></br>
          <div className="text-center">
          <Button variant="success" type="submit" style={buttonStyle}>
            Login
          </Button>
          </div>
          
        </Form>
      </div>
    </div>
  </body>
  );
};

export default LoginPage;
