import React from "react";
import "./styles.css";
import { BrowserRouter, Link, Routes } from "react-router-dom";

function updateTime() {
  const targetDate = new Date("2023-12-31T23:59:59");
  var currentDate = new Date();
  var timeLeft = targetDate - currentDate;

  var day = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
  var hour = Math.floor((timeLeft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  var min = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
  var sec = Math.floor((timeLeft % (1000 * 60)) / 1000);
}

updateTime();

const updateInterval = setInterval(updateTimeLeft, 1000);

function bidding() {
  var balance = 5000;
  var itemName = "Painting";
  var itemDesc = "Antique painting by famous artist XYZ. Painted in 1950.";
  var sName = "Mr.Seller";
  var itemTags = ["painting", "antique", "physical"];
  var sRating = 4.2;

  return (
    <div className="bidContainer">
      <div className="biddingBox">
        <div className="imageInfo">
          <div>
            <img src="/images/user.jpg" className="image" />
          </div>
        </div>
        <div className="bidUserInfo">
          <div className="bidInfo">
            <div className="sellerDetails">
              <p id="SName">Seller: {sName}</p>
              <p id="SRating" style={{ fontSize: "20px" }}>
                Rating: {sRating}
              </p>
            </div>
            <div className="itemDetails">
              <div className="tags">{}</div>
              <p id="itemName">Product: {itemName}</p>
              <p id="itemDesc" style={{ fontSize: "20px" }}>
                {itemDesc}
              </p>
            </div>
            <div className="timeInfo">
              <p id="timeLeft">
                Time Remaining: {day}:{hour}:{min}:{sec}
              </p>
            </div>
          </div>
          <hr />

          <div className="userInfo">
            <p id="balance">User Balance: {balance}currency</p>
          </div>
          <hr />
        </div>
      </div>
    </div>

    /*
    Remove extra padding on right
    make the tags visible using js arrays
    complete the bidding webpage
    */
  );
}

export default bidding;
